package homework;

public class parameter_static {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		parameter_static.baby(10, 20);
		
		parameter_static.gril("big","boy");
		
		parameter_static.silver(25.50,20.22);
		
	}
public static void baby(int a,int b) {
	
	int c = a+b;
	System.out.println(c);
			
	
}
public static void gril (String a,String b) {
	String c = a+b;
	System.out.println(c);
}

public static void silver(double a ,double b) {
double c = a+b;
System.out.println(c);
	
	
}
}
